package com.example.lab_1.service;

import com.example.lab_1.DTOs.TaskDTO;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.mail.SimpleMailMessage;
import org.springframework.mail.javamail.JavaMailSender;
import org.springframework.mail.javamail.MimeMessageHelper;
import org.springframework.stereotype.Component;
import org.springframework.stereotype.Service;

import javax.mail.MessagingException;
import javax.mail.internet.MimeMessage;

@Component
@Slf4j
@Service
@RequiredArgsConstructor
public class MailServiceImpl implements MailService{
    private final JavaMailSender javaMailSender;
    private final TaskService taskService;

    /*
    * This method is used to send email with HTML page.
    * @param to - email address of the recipient
    * @param theme - title of the mail
    * @param taskID - id of the task
    * @param isOwner - used to check is the recipient owner or not
    * */
    @Override
    public void sendMessageWithHTML(String to, String theme, Long taskId, boolean isOwner) {
        try {
            MimeMessage mimeMailMessage=javaMailSender.createMimeMessage();
            TaskDTO task=taskService.getDTOByTaskById(taskId);
            MimeMessageHelper helper = new MimeMessageHelper(mimeMailMessage, true);
            helper.setFrom("mailmachine4@gmail.com");
            helper.setTo(to);
            helper.setSubject(theme);
            String text;
            if (isOwner) text=createHTMLForOvner(task.getName(),task.getDescription(),"http://localhost:8080/tasks/"+taskId);
            else text=createHTMLForOther(task.getName(),task.getDescription());
            helper.setText(text, true);
            javaMailSender.send(mimeMailMessage);
            log.info("request for {} has been sent", to);
        } catch (MessagingException e) {
            e.printStackTrace();
        }
    }
    /*
     * This method is used to create HTML page for email. The owner must have update opportunity.
     * @param name - task name
     * @param descr - task description
     * @param link - link to server (localhost for default or test)
     * */

    private String createHTMLForOvner(String name, String descr, String link){
        return "<!DOCTYPE html>\n" +
                "<html lang=\"en\">\n" +
                "<head>\n" +
                "    <meta charset=\"UTF-8\">\n" +
                "    <title>Title</title>\n" +
                "    <link rel=\"stylesheet\" href=\"/static/mailPage/mail.css\">\n" +
                "    <link rel=\"preconnect\" href=\"https://fonts.googleapis.com\">\n" +
                "    <link rel=\"preconnect\" href=\"https://fonts.gstatic.com\" crossorigin>\n" +
                "    <link href=\"https://fonts.googleapis.com/css2?family=Lexend+Deca:wght@300;400&display=swap\" rel=\"stylesheet\">\n" +
                "</head>\n" +
                "<body style=\"height: 150px;width: 800px;border: 3px solid #6a6a6a;border-radius: 52px;\">\n" +
                "    <table>\n" +
                "        <tr>\n" +
                "            <td colspan=\"3\">\n" +
                "                <p style=\"text-align:center;font-family: 'Lexend Deca', sans-serif; font-size: 25px; margin: 0\">"+name+"</p>\n" +
                "            </td>\n" +
                "        </tr>\n" +
                "        <tr>\n" +
                "            <td>\n" +
                "                <img src=\"data:image/png;base64,\" height=\"100px\">\n" +
                "            </td>\n" +
                "            <td>\n" +
                "                <p style=\"font-family: 'Lexend Deca', sans-serif; font-size: 19px; margin: 0; height: 80px; width: 450px; overflow: hidden\">"+(descr==null?"":descr)+"</p>\n" +
                "            </td>\n" +
                "            <td>\n" +
                "                <div style=\"width: 200px; height: 80px;position: relative;display: flex; justify-content: space-between;\">\n" +
                "                    <a href=\""+link+"\" style=\"display: flex;align-items: center;justify-content: center;height: 80px;width: 80px;margin: 0;padding: 0;border: none;border-radius: 50%;filter: drop-shadow(0 0 3px black);cursor: pointer;background-color: #e2e2e2;\">\n" +
                "                        <img class=\"logo\" src=\"data:image/png;base64,\" height=\"45\">\n" +
                "                    </a>\n" +
                "                </div>\n" +
                "            </td>\n" +
                "        </tr>\n" +
                "    </table>\n" +
                "</body>\n" +
                "</html>";
    }

    /*
     * This method is used to create HTML page for email. The simple people don't have update opportunity.
     * @param name - task name
     * @param descr - task description
     * */
    private String createHTMLForOther(String name, String descr){
        return "<!DOCTYPE html>\n" +
                "<html lang=\"en\">\n" +
                "<head>\n" +
                "    <meta charset=\"UTF-8\">\n" +
                "    <title>Title</title>\n" +
                "    <link rel=\"stylesheet\" href=\"/static/mailPage/mail.css\">\n" +
                "    <link rel=\"preconnect\" href=\"https://fonts.googleapis.com\">\n" +
                "    <link rel=\"preconnect\" href=\"https://fonts.gstatic.com\" crossorigin>\n" +
                "    <link href=\"https://fonts.googleapis.com/css2?family=Lexend+Deca:wght@300;400&display=swap\" rel=\"stylesheet\">\n" +
                "</head>\n" +
                "<body style=\"height: 150px;width: 800px;border: 3px solid #6a6a6a;border-radius: 52px;\">\n" +
                "    <table>\n" +
                "        <tr>\n" +
                "            <td colspan=\"2\">\n" +
                "                <p style=\"text-align:center;font-family: 'Lexend Deca', sans-serif; font-size: 25px; margin: 0\">"+name+"</p>\n" +
                "            </td>\n" +
                "        </tr>\n" +
                "        <tr>\n" +
                "            <td>\n" +
                "                <img src=\"data:image/png;base64,\" height=\"100px\">\n" +
                "            </td>\n" +
                "            <td>\n" +
                "                <p style=\"font-family: 'Lexend Deca', sans-serif; font-size: 19px; margin: 0; height: 80px; width: 450px; overflow: hidden\">"+(descr==null?"":descr)+"</p>\n" +
                "            </td>\n" +
                "        </tr>\n" +
                "    </table>\n" +
                "</body>\n" +
                "</html>";
    }
}
